function inputToGarden(input) {
  const lines = input.split('\n').filter(line => line != "");
  const seeds = lines[0].split(':')[1].trim().split(' ').map(Number);
  let curLineIndex = 1;
  const categories = [];
  let rules = [];
  while (curLineIndex < lines.length) {
    const line = lines[curLineIndex];
    if (line.includes(':')) {
      rules.sort((r1, r2) => r1.range.min - r2.range.min);
      rules = [];
      categories.push(rules);
    } else {
      const [dest, source, range] = line.split(' ') .map(Number);
      rules.push({
        range: {min: source, max: source + range - 1},
        toAdd: - (source - dest),
      })
    }
    curLineIndex++;
  }
  return {seeds, categories};
}

const {seeds, categories} = inputToGarden(input);

// Part 1
function applyRule(nb, rule) {
  if (nb >= rule.range.min && nb <= rule.range.max) {
    return nb + rule.toAdd;
  }
  return nb;
}

function applyRules(nb) {
  for (let catInd = 0; catInd < categories.length; catInd++) {
    for (const rule of categories[catInd]) {
      let converted = applyRule(nb, rule);
      if (converted != nb) {
        nb = converted
        break;
      }

    }
  }
  return nb;
}


let min = Infinity;
for (const seed of seeds) {
  let nb = applyRules(seed);
  if (nb < min) min = nb;
}
console.log(min);

// part 2
function getIntersection(r1, r2) {
  const [smallest, biggest] = r1.min < r2.min ? [r1, r2] : [r2, r1];
  if (smallest.max < biggest.min) return false;
  return {min: biggest.min, max : Math.min(smallest.max, biggest.max)};
}


function applyRulesRange(range) {
  let curRanges = [range];
  for (let catInd = 0; catInd < categories.length; catInd++) {
    const nextRanges = [];
    for (const range of curRanges) {
      let lastRule = null;
      for (const rule of categories[catInd]) {
        const intersection = getIntersection(range, rule.range);
        if (intersection) {
          nextRanges.push({
            min: intersection.min + rule.toAdd,
            max: intersection.max + rule.toAdd,
          });
        }
        if (lastRule) {
          if (rule.range.min > lastRule.range.max + 1) {
            const interval = {min: lastRule.range.max + 1, max: rule.range.min - 1};
            const intersection = getIntersection(range, interval);            ;
            if (intersection) nextRanges.push(intersection);
          }
        }
        lastRule = rule;
      }
      if (range.min < categories[catInd][0].range.min) {
        nextRanges.push({
          min: range.min,
          max: Math.min(categories[catInd][0].range.min - 1, range.max),
        });
      }
      if (range.max > categories[catInd][categories[catInd].length - 1].range.max) {
        nextRanges.push({
          min: Math.max(range.min, categories[catInd][categories[catInd].length - 1].range.max + 1),
          max: range.max,
        });
      }
    }
    curRanges = nextRanges;
  }
  return curRanges;
}


let minPart2 = Infinity;
for (let i = 0; i < seeds.length; i += 2) {
  const range = {min: seeds[i], max: seeds[i] + seeds[i + 1] - 1};
  const finalRange = applyRulesRange(range);
  finalRange.sort((r1, r2) => r1.min - r2.min);
  if (finalRange[0].min < minPart2) minPart2 = finalRange[0].min;
}

